#include <stdlib.h>

struct Node {
int data;
struct Node *next;
};

void add_de(struct Node **head, int data) {
struct Node *new_de = (struct Node *)malloc(sizeof(struct Node));
new_de->data = data;
new_de->next = *head;
*head = new_de;
}

void free_list(struct Node *head) {
struct Node *tmp;
while (head != NULL) {
tmp = head;
head = head->next;
free(tmp);
}
}

int main() {
struct Node *head = NULL;
add_de(&head, 10);
free_list(head);
return 0;
}

