#include <stdlib.h>
#include <stdio.h>

struct Node {
int data;
struct Node *next;
};

struct Node *create_de(int data) {
struct Node *de = (struct Node *)malloc(sizeof(struct Node));
de->data = data;
de->next = NULL;
return de;
}

void add_de(struct Node **head, int data) {
struct Node *new_de = create_de(data);
new_de->next = *head;
*head = new_de;
}

void free_list(struct Node *head) {
struct Node *tmp;
while (head != NULL) {
tmp = head;
head = head->next;
free(tmp);
}
}

int main() {
struct Node *head = NULL;
add_de(&head, 1);
free_list(head);
return 0;
}

